import React from "react";
import Cookies from "js-cookie";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: null,
      jwt: null,
    };
  }
  componentDidMount() {
    fetch("/signin", {
        method: "POST",
        body
    }) 
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            jwt: Cookies.get("jwt"),
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }
  render() {
    const { error, isLoaded, jwt } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return <div>{jwt}</div>;
    }
  }
}

export default Home;
