import React from "react";
import Cookies from "js-cookie";

class SignIn extends React.Component {
  constructor(props, password, email) {
    super(props);
    this.state = {
      error: null,
      isLoaded: null,
      jwt: null,
    };
    this.password = password;
  }
  async componentDidMount() {
    fetch("/signin", {
      method: "POST",
      body: await JSON.stringify({
        email: email,
        password: password,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            jwt: Cookies.get('jwt'),
            result,
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }
  render() {
    const { error, isLoaded, result, jwt } = this.state;
    
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return <div>{}</div>;
    }
  }
}

export default SignIn;
