import React from "react";

class Movies extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: [],
    };
  }

  componentDidMount = async() => {
    fetch("/movies")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            movies: result.info,
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }

  render() {
    const { error, isLoaded, movies } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="page-load">Loading...</div>;
    } else {
      return (
        <ul>
          {movies.map((item) => (
            <li key={item._id}>
              {item.title} {item.author}
            </li>
          ))}
          <button></button>
        </ul>
      );
    }
  }
}

export default Movies;
