import React from "react";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement("#root");
class MovieModal extends React.Component {
  render() {

    return (
      <modal>
        <Modal 
        open={this.props.modalOpen}
        size='small'
        closeOnEscape={true}
        centered={true}
      >
         <Wrapper className="ui raised centered segment">
                <Header icon="browser" content="Update an employee" />
                <div class="ui divider"></div>
                <Modal.Content>

                <Label>Name:  </Label>
                <InputText 
                    placeholder="First Name"
                    type="text"
                    value={firstName}
                    onChange={this.handleChangeInputFirstName}
                />

                <InputText 
                    placeholder="Last Name"
                    type="text"
                    value={lastName}
                    onChange={this.handleChangeInputLastName}
                />

                <Label>Department: </Label>
                <InputText 
                    placeholder="e.g. Marketing"
                    type="text"
                    value={department}
                    onChange={this.handleChangeInputDepartment}
                />

                <Label>Salary: </Label>
                <InputText 
                    placeholder="Min. 2100"
                    type="number"
                    value={salary}
                    step="100"
                    min="2100"
                    onChange={this.handleChangeInputSalary}
                />

                <Label>Currency: </Label>
                <InputText 
                    placeholder="PLN preffered"
                    type="text"
                    value={currency}
                    onChange={this.handleChangeInputCurrency}
                />

                </Modal.Content>
                <Modal.Actions>
                    <div className="ui buttons">
                        <Button onClick={this.handleUpdateEmployee}>Update employee</Button>
                    <div className="or"></div>
                        <CancelButton href={'/employees/list'}>Exit</CancelButton>
                    </div>
                </Modal.Actions>
            </Wrapper>
    </Modal>
      </modal>
    );
  }
}

export default MovieModal;
