import React from "react";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-25%, -50%)",
  },
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement("#root");
class MovieModal extends React.Component {
  render() {
    return (
      <div>
        <Modal
          isOpen={this.props.isOpen}
          contentLabel="Add film"
          onRequestClose={this.props.isClose}
          style={customStyles}
        >
          <div>I am a modal</div>
          <form>
            <div className="row">
              <div className="col">
                <input
                  type="text"
                  className="form-control"
                  placeholder="title"
                ></input>
              </div>
              <div className="col">
                <input
                  type="text"
                  className="form-control"
                  placeholder="author"
                ></input>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <input
                  type="text"
                  className="form-control"
                  placeholder="for example: https://youtu.be/UgNqzbcr2Pw"
                ></input>
              </div>
            </div>
          </form>
          <button type="button" class="btn btn-primary"onClick={this.props.isClose}>close Modal</button>
        </Modal>
      </div>
    );
  }
}

export default MovieModal;
