class MovieDetail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: [],
      isModalOpen: false,
    };
    console.log(this.props.id);
  }

  componentDidMount = () => {
    fetch("/movies/:id")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            movies: result.info,
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  };

  handleAdd = () => {
    this.setState({ isModalOpen: true });
  };

  handleClose = () => {
    this.setState({ isModalOpen: false });
  };
  render() {
      const {movies} = this.state
      console.log(movies.id);
    return (
      <center>
        <div>
          <button
            type="button"
            onClick={this.handleAdd}
            className="btn btn-primary"
