import React from "react";
import MovieEditModal from "../modals/movieEditModal";

class MovieDetail extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      movies: [],
      reviews: [],
      isModalOpen: false,
    };
  }

  componentDidMount = () => {
    fetch(`/movies/${this.props.match.params.id}`)
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            movies: result.info,
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
    fetch("/reviews")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          reviews: result.info,
        });
      });
  };

  handleAdd = () => {
    this.setState({ isModalOpen: true });
  };

  handleClose = () => {
    this.setState({ isModalOpen: false });
  };
  render() {
    const { movies, reviews } = this.state;
    return (
      <center>
        <div>
          <button
            type="button"
            onClick={this.handleAdd}
            className="btn btn-primary"
          >
            Modal
          </button>
          <MovieEditModal
            isOpen={this.state.isModalOpen}
            isClose={this.handleClose}
            id={movies._id}
            title={ movies.title}
            filmUrl= {movies.filmUrl}
            author= {movies.author}
            category= {movies.category}
            description= {movies.description}
          />
          <ul d-flex justify-content-center>
            {reviews.map((item) => (
              <div>
                <form>
                    <div className="form-group">
                        <div className="col-sm-4">
                            <label htmlFor="title">Tytuł komentarza</label>
                            <input type="text" readonly className="form-control" value={item.title} />
                        </div>
                        <div className="col-sm-4">
                            <input type="text" readonly className="form-control" value={item.content} />
                        </div>
                    </div>
                </form>
              </div>
            ))}
          </ul>
        </div>
      </center>
    );
  }
}

export default MovieDetail;
