import React from "react";
import Modal from "react-modal";

import {addFilmService} from "../services/FilmService";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement("#root");
class MovieModal extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      filmUrl: "",
      author: "",
      category:"",
      description:""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState((state) => {
      state[name] = value;
      return state;
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({ submitted: true });
    const { title,filmUrl, author, category, description} = this.state;

    if (!(title && filmUrl && author && category && description)) {
      this.setState({ error: "Fields are required" });
      return;
    }

    addFilmService
      .signIn(title,filmUrl, author, category, description)
      .then((error) => this.setState({ error }));
  };

  render() {
      const {title,filmUrl, author, category, description} = this.state;
    return (
      <div>
        <Modal
          isOpen={this.props.isOpen}
          contentLabel="Add film"
          onRequestClose={this.props.isClose}
          style={customStyles}
          
        >
          <h4>Add your movie</h4>
          <form>
            <div className="row">
              <div className="col">
                <label htmlFor="title">title</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="title"
                  name="title"
                  onChange={this.handleChange}
                value={title}
                ></input>
              </div>
              <div className="col">
                <label htmlFor="url">Author</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="eg. Tim Walles"
                  name="Author"
                  onChange={this.handleChange}
                  value={author}
                ></input>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <label htmlFor="filmUrl">Film url address (youtube)</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="eg. https://youtu.be/UgNqzbcr2Pw"
                  name="filmUrl"
                  onChange={this.handleChange}
                  value={filmUrl}
                ></input>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <label htmlFor="category">Category</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="eg. Horror"
                  name="category"
                  onChange={this.handleChange}
                  value={category}
                ></input>
              </div>
            </div>
            <div className="row">
              <div className="col">
                <label htmlFor="desc">Description</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="film description"
                  name="desc"
                  onChange={this.handleChange}
                  value={description}
                ></input>
              </div>
            </div>
            <br />
            <div className="row">
              <div className="col">
                <button
                  type="button"
                  class="btn btn-outline-dark"
                  type
                >
                  send
                </button>
              </div>
              <div className="col">
                <button
                  type="button"
                  class="btn btn-outline-danger"
                  onClick={this.props.isClose}
                >
                  close
                </button>
              </div>
            </div>
          </form>
        </Modal>
      </div>
    );
  }
}

export default MovieModal;
