import React from "react";
import Cookies from "js-cookie";

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: null,
            jwt: null
        };
    }
  componentDidMount() {
    fetch("/signin")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            jwt: Cookies.get("jwt"),
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
